<?phpnamespace CafeteriasBA\Models;use CafeteriasBA\Interfaz\ICafeteria;use CafeteriasBA\DB\Conexion;use JsonSerializable;use PDO;use Exception;class Cafeteria implements JsonSerializable, ICafeteria {    private $id;    protected $nombre;    protected $direccion;    protected $telefono;    protected $email;    protected $sitio_web;    protected $sucursal;    protected $horario_apertura;    protected $horario_cierre;    protected $valoracion;    protected $votos;    protected $status;    protected $descripcion;    protected $img_portada;    protected $coordenadas_maps;    /**     * Cafeteria constructor.     * @param $id     */    public function __construct($id = null) {        if (!is_null($id)) {            $this->findByPk($id);        }    }    function jsonSerialize() {        // TODO: Implement jsonSerialize() method.           return ['id' => $this->id, 'nombre' => $this->nombre, 'direccion' => $this->direccion, 'email' => $this->email, 'telefono' => $this->telefono, 'sitio_web' => $this->sitio_web, 'sucursal' => $this->sucursal, 'horario_apertura' => $this->horario_apertura, 'horario_cierre' => $this->horario_cierre, 'valoracion' => $this->valoracion, 'votos' => $this->votos, 'status' => $this->status, 'descripcion' => $this->descripcion, 'img_portada' => $this->img_portada, 'coordenadas_maps' => $this->coordenadas_maps];    }/**     * @param $id     */    public function findByPk($id) {        $this->setId($id);        $query = "SELECT * FROM t_cafeteria                  WHERE ID_cafeteria = ?";        $stmt = Conexion::getStatement($query);        $stmt->execute(array($this->getId()));        $this->loadData($stmt->fetch(PDO::FETCH_ASSOC));    }/**     * @param $fila     */    protected function loadData($fila) { /*     * *         * Set Load Data : Carga los campos de la clase usuario.         */        $this->setId($fila['ID_cafeteria']);        $this->setNombre($fila['nombre']);        $this->setDireccion($fila['direccion']);        $this->setEmail($fila['email']);        $this->setTelefono($fila['telefono']);        $this->setSitioWeb($fila['sitio_web']);        $this->setSucursal($fila['sucursal']);        $this->setHorarioApertura($fila['horario_apertura']);        $this->setHorarioCierre($fila['horario_cierre']);        $this->setValoracion($fila['valoracion']);        $this->setVotos($fila['votos']);        $this->setStatus($fila['status']);        $this->setDescripcion($fila['descripcion']);        $this->setimgportada($fila['img_portada']);        $this->setCoordenadasMaps($fila['coordenadas_maps']);    }/**     * @return array     */    public static function getAll() {        // TODO: Implement getAll() method.            $query = "SELECT * FROM t_cafeteria WHERE status = 'activo'";        $stmt = Conexion::getStatement($query);        $stmt->execute();        while ($fila = $stmt->fetch(PDO::FETCH_ASSOC)) {            $cafeteria = new Cafeteria();            $cafeteria->LoadData($fila);            $salida[] = $cafeteria;        }        return $salida;    }    public static function getSugerencias() {        $query = "SELECT * FROM t_cafeteria WHERE status = 'pendiente'";        $stmt = Conexion::getStatement($query);        $stmt->execute();        //var_dump($stmt->rowCount());        if ($stmt->rowCount() > 0) {            while ($fila = $stmt->fetch(PDO::FETCH_ASSOC)) {                $cafeteria = new Cafeteria();                $cafeteria->LoadData($fila);                $salida[] = $cafeteria;            }            $output = [                'status' => 1,                'data' => $salida            ];            //var_dump($output);             return $output;        } else {            $output = [                'status' => 0,                'data' => null            ];            //var_dump($output);             return $output;        }    }    public static function topRank() {        $query = "SELECT * FROM t_cafeteria WHERE status = 'activo' ORDER BY valoracion DESC LIMIT 3";        $stmt = Conexion::getStatement($query);        $stmt->execute();        while ($fila = $stmt->fetch(PDO::FETCH_ASSOC)) {            $cafeteria = new Cafeteria();            $cafeteria->LoadData($fila);            $salida[] = $cafeteria;        } return $salida;    }    /**     * @param $id     * @return array     */    public static function eliminarCafeteria($id) {        // TODO: Implement eliminarCafeteria() method.        $query = "UPDATE t_cafeteria SET status = 'inactivo' WHERE ID_cafeteria = ?";        $stmt = Conexion::getStatement($query);        $stmt->execute([$id]);        if ($stmt) {            $salida = ['status' => 1, 'message' => 'Cafeteria eliminada'];        } else {            $salida = ['status' => 0, 'message' => 'Hubo un error en la eliminacion'];        } return $salida;    }/**     * @param $id     * @return array     */    public static function verCafeteria($id) {        // TODO: Implement verCafeteria() method.         $query = "SELECT * FROM t_cafeteria WHERE ID_cafeteria = ?";        $stmt = Conexion::getStatement($query);        $stmt->execute([$id]);        $salida = [];        while ($fila = $stmt->fetch(PDO::FETCH_ASSOC)) {            $cafeteria = new Cafeteria();            $cafeteria->setId($fila['ID_cafeteria']);            $cafeteria->loadData($fila);            $salida[] = $cafeteria;        } return $salida;    }/**        En base al id de un usuario obtenemos si tiene cafeterias creadas     * @param $id     * @return array     */    public function verCafeteriaPorUsuario($id_usuario) {        // TODO: Implement verCafeteria() method.         $query = "SELECT *  FROM t_cafeteria WHERE id_usuario = ?";        $stmt = Conexion::getStatement($query);        $stmt->execute([$id_usuario]);        $salida = [];        while ($fila = $stmt->fetch(PDO::FETCH_ASSOC)) {            $cafeteria = new Cafeteria();            $cafeteria->setId($fila['ID_cafeteria']);            $cafeteria->loadData($fila);            $salida[] = $cafeteria;        } return $salida;    }    public static function getByEmail($email) {        // TODO: Implement verCafeteria() method.           $query = "SELECT * FROM t_cafeteria WHERE email = ?";        $stmt = Conexion::getStatement($query);        $stmt->execute([$email]);        if ($fila = $stmt->fetch(PDO::FETCH_ASSOC)) {            $cafeteria = new Cafeteria();            $cafeteria->loadData($fila);            return $cafeteria;        } else {            return null;        }    }    /**     * Buscar en la db de acuerdo a parametros pasados     * @param array data     * @return \CafeteriasBA\Models\Cafeteria     */    public static function search($data) {        // TODO: Implement verCafeteria() method.                                 $nombre = strtolower($data['nombre']);               $sucu = strtolower($data['zona']);                $query = "SELECT * FROM t_cafeteria WHERE  1 = 1 ";                if($data['nombre'] != ''){                        $query.='and LOWER(nombre) LIKE "%'.trim($nombre).'%"';        }                        if($data['zona'] != ''){                        $query.='and LOWER(sucursal) LIKE "%'.trim($sucu).'%"';                    }                       $stmt = Conexion::getStatement($query);                $stmt->execute();        while ($fila = $stmt->fetch(PDO::FETCH_ASSOC)) {            $cafeteria = new Cafeteria();            $cafeteria->LoadData($fila);            $salida[] = $cafeteria;                    } if ($salida) {            return $salida;        } else {            return null;        }    }/**     * @param $data     * @return Cafeteria     * @throws Exception     */    public static function editarCafeteria($data) {        // TODO: Implement editarCafeteria() method.         $img_portada = 'noportada.jpg';        if (isset($data['img_portada'])) {            if ($data['img_portada'] != '') {                $img_portada = $data['img_portada'];            } else {                $img_portada = 'noportada.jpg';            }        } $query = "UPDATE t_cafeteria SET        nombre=:nombre,        direccion=:direccion,        telefono=:telefono,        email=:email,        sitio_web=:sitio_web,        sucursal=:sucursal,        horario_apertura=:horario_apertura,        horario_cierre=:horario_cierre,        img_portada=:img_portada, coordenadas_maps=:coordenadas_maps, descripcion=:descripcion        WHERE ID_cafeteria=:idcafeteria";        $stmt = Conexion::getStatement($query);        $exito = $stmt->execute(['nombre' => $data['nombre'], 'direccion' => $data['direccion'], 'telefono' => $data['telefono'], 'email' => $data['email'], 'sitio_web' => $data['sitioWeb'], 'sucursal' => $data['sucursal'], 'horario_apertura' => $data['horarioApertura'], 'horario_cierre' => $data['horarioCierre'], 'img_portada' => $img_portada, 'coordenadas_maps' => $data['coordenadas_maps'],'descripcion'=> $data['descripcion'], 'idcafeteria' => $data['idcafeteria']]);        if ($exito) {            $cafeteria = new Cafeteria($data['idcafeteria']);            //$cafeteria->loadData($data);              $salida = $cafeteria;            return $salida;        } else {            throw new Exception('No se ha podido editar la cafeteria.');        }    }    public static function aprobarCafeteria($data) {        $query = "UPDATE t_cafeteria SET status=:status WHERE ID_cafeteria=:id";        $stmt = Conexion::getStatement($query);        $status = 'activo';        $exito = $stmt->execute(['status' => $status, 'id' => $data['id'],]);        if ($exito) {            $cafeteria = new Cafeteria($data['id']);            //$cafeteria->loadData($data);             $salida = $cafeteria;            return $salida;        } else {            throw new Exception('No se ha podido aprobar la cafeteria.');        }    }/*     * *     * @param $data     * @return bool     * @throws Exception     */    public static function crearCafeteria($data) {                      $query = "INSERT INTO t_cafeteria (nombre,direccion,telefono,email,sitio_web,sucursal,horario_apertura,horario_cierre,valoracion,votos,status,descripcion,img_portada,coordenadas_maps) VALUES (:nombre,:direccion,:telefono,:email,:sitio_web,:sucursal,:horario_apertura,:horario_cierre,:valoracion,:votos,:status,:descripcion,:img_portada,:coordenadas_maps)";        $valoracion = 0;        $votos = 0;        $status = 'activo';        $stmt = Conexion::getStatement($query);        $exito = $stmt->execute([            'nombre' => $data['nombre'],            'direccion' => $data['direccion'],            'telefono' => $data['telefono'],            'email' => $data['email'],            'sitio_web' => $data['sitioWeb'],            'sucursal' => $data['sucursal'],            'horario_apertura' => $data['horarioApertura'],            'horario_cierre' => $data['horarioCierre'],            'valoracion' => $valoracion,            'votos' => $votos,            'status' => $status,            'descripcion' => $data['descripcion'],            'img_portada' => $data['img_portada'],            'coordenadas_maps' => $data['coordenadas_maps'],        ]);        if ($exito) {            return true;        } else {            throw new Exception('no se a podido crear la cafeteria');        }    }    public static function sugerirCafeteria($data) {                        $query = "INSERT INTO t_cafeteria (nombre,direccion,email,sucursal,horario_apertura,horario_cierre,status,valoracion,votos,sitio_web,telefono,img_portada) VALUES (:nombre,:direccion,:email,:sucursal,:horario_apertura,:horario_cierre,:status,:valoracion,:votos,:sitio_web,:telefono,:img_portada)";        $valoracion = 0;        $votos = 0;        $status = 'pendiente';        $img_portada = 'no_imagen.jpg';        $stmt = Conexion::getStatement($query);        $exito = $stmt->execute([            'nombre' => $data['nombre'],            'direccion' => $data['direccion'],            'email' => $data['email'],            'sucursal' => $data['sucursal'],            'horario_apertura' => $data['horarioApertura'],            'horario_cierre' => $data['horarioCierre'],            'status' => $status,            'valoracion' => $valoracion,            'votos' => $votos,            'sitio_web' => $data['sitioWeb'],            'telefono' => $data['telefono'],            'img_portada' => $img_portada        ]);        if ($exito) {            return true;        } else {            throw new Exception('no se a podido crear la cafeteria');        }    }        /**     * hace la cuenta de la valoracion en base a comentarios totales y puntaje     * @param type $data     * @return boolean     * @throws Exception     */    public static function valoracionCafeteria($idcafeteria) {                        $query = "SELECT IFNULL(ROUND(sum(puntaje)/count(id)),0) as puntaje FROM t_comentarios"                . " WHERE id_cafeteria = $idcafeteria";                $stmt = Conexion::getStatement($query);        $exito = $stmt->execute();                 while ($exito = $stmt->fetch(PDO::FETCH_ASSOC)) {                          return($exito["puntaje"]);                    }          }        /**     * hace un update de la valoracion de la cafeteria     * @param type $data     * @return boolean     * @throws Exception     */    public static function updatePuntaje($idcafeteria ,$puntaje) {                        $query = "Update t_cafeteria set valoracion = ".$puntaje ."WHERE ID_cafeteria = ".$idcafeteria;                $stmt = Conexion::getStatement($query);        $exito = $stmt->execute();               if($exito){           return true;       }          }/**     * @return mixed     */    public function getId() {        return $this->id;    }/**     * @param mixed $id     */    public function setId($id) {        $this->id = $id;    }/**     * @return mixed     */    public function getNombre() {        return $this->nombre;    }/**     * @param mixed $nombre     */    public function setNombre($nombre) {        $this->nombre = $nombre;    }/**     * @return mixed     */    public function getDireccion() {        return $this->direccion;    }/**     * @param mixed $direccion     */    public function setDireccion($direccion) {        $this->direccion = $direccion;    }/**     * @return mixed     */    public function getTelefono() {        return $this->telefono;    }/**     * @param mixed $telefono     */    public function setTelefono($telefono) {        $this->telefono = $telefono;    }/**     * @return mixed     */    public function getEmail() {        return $this->email;    }/**     * @param mixed $email     */    public function setEmail($email) {        $this->email = $email;    }/**     * @return mixed     */    public function getSitioWeb() {        return $this->sitio_web;    }/**     * @param mixed $sitio_web     */    public function setSitioWeb($sitio_web) {        $this->sitio_web = $sitio_web;    }/**     * @return mixed     */    public function getSucursal() {        return $this->sucursal;    }/**     * @param mixed $sucursal     */    public function setSucursal($sucursal) {        $this->sucursal = $sucursal;    }/**     * @return mixed     */    public function getHorarioApertura() {        return $this->horario_apertura;    }/**     * @param mixed $horario_apertura     */    public function setHorarioApertura($horario_apertura) {        $this->horario_apertura = $horario_apertura;    }/**     * @return mixed     */    public function getHorarioCierre() {        return $this->horario_cierre;    }/**     * @param mixed $horario_cierre     */    public function setHorarioCierre($horario_cierre) {        $this->horario_cierre = $horario_cierre;    }/**     * @return mixed     */    public function getValoracion() {        return $this->valoracion;    }/**     * @param mixed $valoracion     */    public function setValoracion($valoracion) {        $this->valoracion = $valoracion;    }/**     * @return mixed     */    public function getVotos() {        return $this->votos;    }/**     * @param mixed $votos     */    public function setVotos($votos) {        $this->votos = $votos;    }/**     * @return mixed     */    public function getStatus() {        return $this->status;    }/**     * @param mixed $status     */    public function setStatus($status) {        $this->status = $status;    }/**     * @return mixed     */    public function getdescripcion() {        return $this->descripcion;    }/**     * @param mixed $status     */    public function setdescripcion($descripcion) {        $this->descripcion = $descripcion;    }/**     * @param mixed $nombre     */    public function setimgportada($imgportada) {        $this->img_portada = $imgportada;    }/**     * @return mixed     */    public function getimgportada() {        return $this->img_portada;    }    /**     * @param mixed $nombre     */    public function setCoordenadasMaps($coordenadas_maps) {        $this->coordenadas_maps = $coordenadas_maps;    }/**     * @return mixed     */    public function getCoordenadasMaps() {        return $this->coordenadas_maps;    }}